import React,{useEffect,useState}  from "react";
import { Container, Row, Col } from 'reactstrap';
import './App.css'; 

const opsMeta = ['+', '-', '*', '/']; 

export default function Calculator(){
	const [result, setResult] = useState('');  
    const sumReducer = (acc, value) => acc + value;
    const mulReducer = (acc, value) => acc * value;
    const divReducer = (acc, value) => acc / value;
    const subReducer = (acc, value) => acc - value;

    const sum = (args) => args.reduce(sumReducer);
    const mul = (args) => args.reduce(mulReducer);
    const div = (args) => args.reduce(divReducer);
    const sub = (args) => args.reduce(subReducer);

    return(
		<div> 
			 <Container>
      <Row>
        <Col>.col</Col>
      </Row>
      <Row>
        <Col>.col</Col>
        <Col>.col</Col>
        <Col>.col</Col>
        <Col>.col</Col>
      </Row>
      <Row>
        <Col xs="3">.col-3</Col>
        <Col xs="auto">.col-auto - variable width content</Col>
        <Col xs="3">.col-3</Col>
      </Row>
      <Row>
        <Col xs="6">.col-6</Col>
        <Col xs="6">.col-6</Col>
      </Row>
      <Row>
        <Col xs="6" sm="4">.col-6 .col-sm-4</Col>
        <Col xs="6" sm="4">.col-6 .col-sm-4</Col>
        <Col sm="4">.col-sm-4</Col>
      </Row>
      <Row>
        <Col sm={{ size: 6, order: 2, offset: 1 }}>.col-sm-6 .order-sm-2 .offset-sm-1</Col>
      </Row>
      <Row>
        <Col sm="12" md={{ size: 6, offset: 3 }}>.col-sm-12 .col-md-6 .offset-md-3</Col>
      </Row>
      <Row>
        <Col sm={{ size: 'auto', offset: 1 }}>.col-sm-auto .offset-sm-1</Col>
        <Col sm={{ size: 'auto', offset: 1 }}>.col-sm-auto .offset-sm-1</Col>
      </Row>
    </Container>
				<Show/>
				<Reset value=""/> 
			   <Container>
				<Row>
                   <Col xs="3">
				   		<Zero value="0"/> 
                   </Col> 
                   <Col xs="3">
				  		 <One value="1"/> 
                   </Col> 
                   <Col xs="3">
				  		 <Two value="2"/> 
                   </Col> 
               </Row>

</Container>

				<Three value="3"/>
				<Four value="4"/>
				<Five value="5"/>
				<Six value="6"/>
				<Seven value="7"/>
				<Eight value="8"/>
				<Nine value="9"/>

		</div>
	);
	//ekran
	function Show(){
		return(
			<div>Ekran olicek burası  
				<h1>{result}</h1>
			</div> 
	)};

	//reset
	function Reset () {
		return(<div>
			<button onClick={()=>setResult("")} key="reset" value="reset">Reset</button>
		</div>)
	}; 

	//Hesap
	function Colcu(props){
		return(<div>
			<h1>Hello, {props.name}</h1>
		</div>)
	};

	//Sayılar
	function Zero(props){return(
		<div>
			<button key={props.value} onClick={()=>setResult(result+props.value)}>{props.value}</button> 
		</div>
		);}  

	function One( props){
		return(
			<div>
			<button key={props.value} onClick={()=>setResult(result+props.value)}>{props.value}</button> 
			</div>
		); 
	}
	function Two( props){
		return(
			<div>
			<button key={props.value} onClick={()=>setResult(result+props.value)}>{props.value}</button> 
			</div>
		); 
	}
	function Three( props){
		return(
			<div>
			<button key={props.value} onClick={()=>setResult(result+props.value)}>{props.value}</button> 
			</div>
		); 
	}
	function Four( props){
		return(
			<div>
			<button key={props.value} onClick={()=>setResult(result+props.value)}>{props.value}</button> 
			</div>
		); 
	}
	function Five( props){
		return(
			<div>
			<button key={props.value} onClick={()=>setResult(result+props.value)}>{props.value}</button> 
			</div>
		); 
	}
	function Six( props){
		return(
			<div>
			<button key={props.value} onClick={()=>setResult(result+props.value)}>{props.value}</button> 
			</div>
		); 
	}
	function Seven( props){
		return(
			<div>
			<button key={props.value} onClick={()=>setResult(result+props.value)}>{props.value}</button> 
			</div>
		); 
	}
	function Eight( props){
		return(
			<div>
			<button key={props.value} onClick={()=>setResult(result+props.value)}>{props.value}</button> 
			</div>
		); 
	}
	function Nine( props){
		return(
			<div>
			<button key={props.value} onClick={()=>setResult(result+props.value)}>{props.value}</button> 
			</div>
		); 
	}
