import logo from './logo.svg';
import './App.css';
import SimpleCalculator from './SimpleCalculator'
import {useState,useEffect} from "react";

function App() {
	const [count,setCount]=useState(0);
	useEffect(() => {
		document.title = `${count} kere tıkladınız`;
	});
	return (
		<div className="App"> 

			<p>{count} kere tıklandı.</p>
			<button className='btn btn-primary' onClick={()=>setCount(count+1)}>Tıkla</button>
			{/* <SimpleCalculator/> */}
		</div>
	);

}

export default App;
