					<Col style={{ backgroundColor: 'pink'}}  md="0.1">
						<Seven value="7"/> 
					</Col>
					<Col style={{ backgroundColor: 'pink'}}  md="0.1">
						<Eight value="8"/>
					</Col>
				</Row>  

			<Nine value="9"/>

		</div>
	);
	//ekran
	function Show(){
		return(
			<div>Ekran olicek burası  
				<h1>{result}</h1>
			</div> 
	)};

	//reset
	function Reset () {
		return(<div>
			<button onClick={()=>setResult("")} key="reset" value="reset">Reset</button>
		</div>)
	}; 

	//Hesap
	function Colcu(props){
		return(<div>
			<h1>Hello, {props.name}</h1>
		</div>)
	};

	//Sayılar
	function Zero(props){return(
		<div>
			<button style={{backgroundColor:'pink',border:"0"}} key={props.value} onClick={()=>setResult(result+props.value)}>{props.value}</button> 
		</div>
		);}  

	function One( props){
		return(
			<div>
			<button style={{backgroundColor:'pink',border:"0"}} key={props.value} onClick={()=>setResult(result+props.value)}>{props.value}</button> 
			</div>
		); 
	}
	function Two( props){
		return(
			<div>
			<button style={{backgroundColor:'pink',border:"0"}} key={props.value} onClick={()=>setResult(result+props.value)}>{props.value}</button> 
