import React,{useEffect,useState}  from "react";
import { Container, Row, Col } from 'reactstrap';
import './App.css'; 

const opsMeta = ['+', '-', '*', '/']; 

export default function Calculator(){
	const [result, setResult] = useState('');  
    const sumReducer = (acc, value) => acc + value;
    const mulReducer = (acc, value) => acc * value;
    const divReducer = (acc, value) => acc / value;
    const subReducer = (acc, value) => acc - value;

    const sum = (args) => args.reduce(sumReducer);
    const mul = (args) => args.reduce(mulReducer);
    const div = (args) => args.reduce(divReducer);
    const sub = (args) => args.reduce(subReducer);

    return(
		<div> 
			<Show/>  
			<Reset value=""/>   
				<Row className="justify-content-md-center" xs="auto"> 
					<Col style={{ backgroundColor: 'pink'}}  md="0.1">
						<One value="1"/> 
					</Col>
					<Col style={{ backgroundColor: 'pink'}}  md="0.1">
						<Two value="2"/> 
					</Col>
					<Col style={{ backgroundColor: 'pink'}} md="0.1">
						<Three value="3"/> 
					</Col>
					<Col style={{ backgroundColor: 'pink'}} md="0.1">
						<Addition value="+"/>
					</Col>
				</Row>  
				<Row className="justify-content-md-center" xs="auto">

					<Col style={{ backgroundColor: 'pink'}}  md="0.1">
						<Four value="4"/>
					</Col>
					<Col style={{ backgroundColor: 'pink'}}  md="0.1">
						<Five value="5"/> 
					</Col>
					<Col style={{ backgroundColor: 'pink'}} md="0.1">
						<Six value="6"/> 
					</Col>
					<Col style={{ backgroundColor: 'pink'}} md="0.1">
						<Extraction value="-"/>
					</Col>
				</Row>  
				<Row className="justify-content-md-center" xs="auto">

					<Col style={{ backgroundColor: 'pink'}}  md="0.1">
						<Seven value="7"/> 
					</Col>
					<Col style={{ backgroundColor: 'pink'}}  md="0.1">
						<Eight value="8"/>
					</Col>
					<Col style={{ backgroundColor: 'pink'}} md="0.1">
						<Nine value="9"/>
					</Col>
					<Col style={{ backgroundColor: 'pink'}} md="0.1">
						<Extraction value="/"/>
					</Col>
				</Row>  
				<Row className="justify-content-md-center" xs="auto">
					<Col style={{ backgroundColor: 'pink'}} md="0.1">
						<Zero value="0"/> 
					</Col>
					<Col style={{ backgroundColor: 'pink'}} md="0.1">
						<Zero value="."/> 
					</Col>
					<Col style={{ backgroundColor: 'pink'}} md="0.1">
						<Zero value="0"/> 
					</Col>
					<Col style={{ backgroundColor: 'pink'}} md="0.1">
						 <Multiplay value="calcu"/>
					</Col>
				</Row>
				<Col style={{ backgroundColor: 'pink'}} md="0.1">
						 <Calcu value="calcu"/>
					</Col>
		</div>
	);
	//ekran
	function Show(){
		return(
			<div>Ekran olicek burası  
				<h1>{result}</h1>
			</div> 
	)};

	//reset
