import React,{useState}  from "react";
import { Row, Col } from 'reactstrap';
import './App.css'; 

const opsMeta = ['+', '-', '*', '/']; 


export default function Calculator(){
	const [result, setResult] = useState('');  
    const sumReducer = (acc, value) => acc + value;
    const mulReducer = (acc, value) => acc * value;
    const divReducer = (acc, value) => acc / value;
    const subReducer = (acc, value) => acc - value;

    const sum = (args) => args.reduce(sumReducer);
    const mul = (args) => args.reduce(mulReducer);
    const div = (args) => args.reduce(divReducer);
    const sub = (args) => args.reduce(subReducer);
	const addition=(arr)=>{
		arr[0]
	}
	var characters=result.split("");
	// var character=characters[characters.length-1];

	function calcu(result) {
		console.log(result);
		if (characters.includes("+")) {
			var temp_arr=result.split("+");
			const arrInt = temp_arr.map(el => parseInt(el)); 
			console.log(arrInt);
			return sum(arrInt);
		} 
console.log(result);
		return result;

	} 
	function singleOp(props) { 
		if (!characters.includes('+') && !characters.includes("-") && !characters.includes("*") && !characters.includes("/")) {
			setResult(result+props.value);
		}
	}

    return(
		<div> 
			<Show/>  
			<Reset value=""/>   
				<Row className="justify-content-md-center" xs="auto"> 
					<Col style={{ backgroundColor: 'pink'}}  md="0.1">
						<Numbers value="1"/> 
					</Col>
					<Col style={{ backgroundColor: 'pink'}}  md="0.1">
						<Numbers value="2"/> 
					</Col>
					<Col style={{ backgroundColor: 'pink'}} md="0.1">
						<Numbers value="3"/> 
					</Col>
					<Col style={{ backgroundColor: 'pink'}} md="0.1">
						<Process value="+"/>
					</Col>
				</Row>  
				<Row className="justify-content-md-center" xs="auto">

					<Col style={{ backgroundColor: 'pink'}}  md="0.1">
						<Numbers value="4"/>
					</Col>
					<Col style={{ backgroundColor: 'pink'}}  md="0.1">
						<Numbers value="5"/> 
					</Col>
					<Col style={{ backgroundColor: 'pink'}} md="0.1">
						<Numbers value="6"/> 
					</Col>
					<Col style={{ backgroundColor: 'pink'}} md="0.1">
								<Process value="-"/>
					</Col> 

				</Row>  
				<Row className="justify-content-md-center" xs="auto">

					<Col style={{ backgroundColor: 'pink'}}  md="0.1">
						<Numbers value="7"/> 
					</Col>
					<Col style={{ backgroundColor: 'pink'}}  md="0.1">
						<Numbers value="8"/>
					</Col>
					<Col style={{ backgroundColor: 'pink'}} md="0.1">
						<Numbers value="9"/>
					</Col>
					<Col style={{ backgroundColor: 'pink'}} md="0.1"> 
						<Process  value="/"/>
					</Col>
				</Row>  
				<Row className="justify-content-md-center" xs="auto">
					<Col style={{ backgroundColor: 'pink'}} md="0.1">
						<Numbers value="0"/> 
					</Col>
					<Col style={{ backgroundColor: 'pink'}} md="0.1">
						<Numbers value="."/> 
					</Col>
					<Col style={{ backgroundColor: 'pink'}} md="0.1">
						 <Calcu value="="/>
					</Col>
					<Col style={{ backgroundColor: 'pink'}} md="0.1">
						 <Process value="*"/>
					</Col>
				</Row>
		</div>
	);
	//ekran
	function Show(){
		return(
			<div>Ekran olicek burası  
				<h1>{result}</h1>
			</div> 
	)};

	//sıfırlama
	function Reset () {
		return(
			<div>
				<button onClick={()=>setResult("")} key="reset" value="reset">Reset</button>
			</div>
		);
	}

	//Hesap
	function Calcu(props){
		return(<div> 
			<button style={{backgroundColor:'pink',border:"0"}} key={props.value} onClick={()=>calcu(result)}>{props.value}</button> 
		</div>)
	};

	//Sayılar
	function Numbers(props){return(
		<div>
			<button style={{backgroundColor:'pink',border:"0"}} key={props.value} onClick={()=>setResult(result+props.value)}>{props.value}</button> 
		</div>
		);}  

	//işlemler
	function Process( props) {     
		return(
			<div>
				<button style={{backgroundColor:'pink',border:"0"}} key={props.value} onClick={()=>singleOp(props)}>{props.value}</button> 
			</div>
		);
	} 
}
